import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios'; // Import Axios
import { Modal } from 'react-bootstrap'; // Import Bootstrap Modal

// Create Axios instance with baseURL
const axiosInstance = axios.create({
  baseURL: 'http://localhost:8081/api/v1', // Adjust the URL according to your backend
});

export default function Navbar() {
  const navigate = useNavigate();
  const location = useLocation();
  const [showModal, setShowModal] = useState(false); // State to control modal visibility

  const handleSignOut = () => {
    // Send request to backend to logout using Axios
    axiosInstance.post('/auth/logout', null, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
        'Content-Type': 'application/json'
      },
    })
    .then(response => {
      console.log(response.data.message); // Log the response from backend
      // Clear token from local storage
      localStorage.removeItem('token');
      // Redirect to the home page
      navigate('/');
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const handleProfileClick = () => {
    setShowModal(true); // Show the profile modal when profile icon is clicked
  };

  const handleCloseModal = () => {
    setShowModal(false); // Close the profile modal
  };

  return (
    <div className="top-navbar">
      <div className="center-links">
        {/* Render profile icon if the current location starts with "/admin/" */}
        {location.pathname.startsWith("/admin") && (
          <div className="profile-icon" onClick={handleProfileClick}>
            {/* Use Bootstrap's user circle icon */}
            <i className="bi bi-person-circle" style={{ fontSize: '2em', color: '#007bff', cursor: 'pointer' }}></i>
          </div>
        )}
      </div>
      <div className="right-links">
        <span className="signout-link" onClick={handleSignOut}>
          Log out
        </span>
      </div>

      {/* Profile Modal */}
      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Replace "/path/to/profile-image.jpg" with the actual path to your profile image */}
          <img src="/path/to/profile-image.jpg" alt="Profile" style={{ maxWidth: '100%' }} />
        </Modal.Body>
      </Modal>
    </div>
  );
}
