import React, { useState } from 'react';

const DocumentsPage = () => {
  // State for consent status
  const [consents, setConsents] = useState({});

  // State for showing/hiding hospital selection modal
  const [showHospitalModal, setShowHospitalModal] = useState(false);

  // State for selected hospital
  const [selectedHospital, setSelectedHospital] = useState(null);

  // List of hospitals (replace with actual data)
  const hospitals = [
    { id: 1, name: 'Hospital A' },
    { id: 2, name: 'Hospital B' },
    { id: 3, name: 'Hospital C' },
    // Add more hospitals as needed
  ];

  // Function to handle consent request for a document
  const handleAskForConsent = (documentId) => {
    // Logic to request consent (could be an API call)
    // For simplicity, directly setting consent to true
    setConsents(prevConsents => ({
      ...prevConsents,
      [documentId]: true
    }));
  };

  // Function to handle hospital selection
  const handleHospitalSelect = (hospital) => {
    setSelectedHospital(hospital);
    setShowHospitalModal(false);
    setConsents(true); // For simplicity, set consent to true after hospital selection
  };

  // Function to handle download button click
  const handleDownload = (documentId) => {
    if (consents[documentId]) {
      // Logic to initiate download
      // For simplicity, just log a message
      console.log('Downloading document...');
    } else {
      // Display error message or take appropriate action
      console.log('You need to have consent to download this document.');
    }
  };

  return (
    <div className='main-content'>
    <div>
      <h1>Documents</h1>
      <ul>
        <li>
          Document 1
          {!consents['document1'] && (
            <button onClick={() => handleAskForConsent('document1')}>Ask for Consent</button>
          )}
          <button onClick={() => handleDownload('document1')}>Download</button>
        </li>
        <li>
          Document 2
          {!consents['document2'] && (
            <button onClick={() => handleAskForConsent('document2')}>Ask for Consent</button>
          )}
          <button onClick={() => handleDownload('document2')}>Download</button>
        </li>
        {/* Add more documents as needed */}
      </ul>

      {/* Hospital selection modal */}
      {showHospitalModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowHospitalModal(false)}>&times;</span>
            <h2>Select Hospital</h2>
            <ul>
              {hospitals.map(hospital => (
                <li key={hospital.id} onClick={() => handleHospitalSelect(hospital)}>
                  {hospital.name}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
    </div>
  );
};

export default DocumentsPage;
