import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import SideNavbar from "../../components/Doctor/sidenavbar";
import Navbar from "../../components/Doctor/Navbar";
import "../../css/Doctor/ddashboard.css";

export default function UploadSchedule() {
  // Define dummy schedule data
  const dummySchedule = {
    "slots": [
      [2024, 4, 5, 11, 15],
      [2024, 4, 6, 15, 45],
      [2024, 4, 7, 15, 0],
      [2024, 4, 6, 15, 45],
      [2024, 4, 5, 15, 0]
    ],
    "appointments": [
      { id: 1, date: '2024,4,5', time: '09:00', patientName: 'John Doe' },
      { id: 2, date: '2024,4,6', time: '10:30', patientName: 'somebody someone' },
      { id: 3, date: '2024,4,7', time: '11:15', patientName: 'Alice Smith' },
      { id: 5, date: '2024,4,6', time: '09:00', patientName: 'John Doe' },
    ]
  };

  // State for schedule data
  const [schedule, setSchedule] = useState([]);

  // State for editing mode
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Initialize schedule with dummy data
    generateSchedule();
  }, []);

  // Function to generate schedule table
  const generateSchedule = () => {
    const currentDate = new Date();
    const next7Days = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(currentDate);
      date.setDate(date.getDate() + i);
      next7Days.push(date);
    }

    const timeSlots = [];
    for (let hour = 9; hour < 16; hour++) {
      for (let minutes = 0; minutes < 60; minutes += 45) {
        const time = `${hour}:${minutes === 0 ? '00' : minutes}`;
        timeSlots.push(time);
      }
    }

    const newSchedule = next7Days.map(date => {
      const dateString = `${date.getFullYear()}-${(date.getMonth() + 1).toString().padStart(2, '0')}-${date.getDate().toString().padStart(2, '0')}`;
      const availability = timeSlots.map(time => {
        const slot = dummySchedule.slots.find(slot => {
          const slotDate = new Date(slot[0], slot[1] - 1, slot[2]);
          const slotTime = `${slot[3]}:${slot[4]}`;
          return slotDate.toDateString() === date.toDateString() && slotTime === time;
        });

        if (slot) {
          return 'available';
        }

        const appointment = dummySchedule.appointments.find(appointment => {
          const appointmentDate = new Date(appointment.date.replace(/,/g, '-'));
          return appointmentDate.toDateString() === date.toDateString() && appointment.time === time;
        });

        return appointment ? 'booked' : 'busy';
      });

      return { date: dateString, availability };
    });

    setSchedule(newSchedule);
  };

  // Function to toggle editing mode
  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Function to handle cell click (toggle availability)
  const handleCellClick = (dateIndex, timeIndex) => {
    const updatedSchedule = [...schedule];
    const cellValue = updatedSchedule[dateIndex].availability[timeIndex];

    if (isEditing && cellValue !== 'booked') {
      updatedSchedule[dateIndex].availability[timeIndex] =
        cellValue === 'available' ? 'busy' : 'available';
      setSchedule(updatedSchedule);
    }
  };

  // Function to get CSS class for cell based on availability
  const getClassForAvailability = (availability) => {
    switch (availability) {
      case 'available':
        return 'table-success bg-success text-white';
      case 'booked':
        return 'table-danger bg-danger text-dark';
      case 'busy':
        return 'table-secondary bg-secondary text-white';
      default:
        return '';
    }
  };

  // Function to simulate saving the schedule (replace with actual logic)
  const handleSubmit = async () => {
    alert('Saving schedule...');

    // Replace with your actual saving logic (e.g., API call)
    // This example simulates a successful save after 2 seconds
    await new Promise((resolve) => setTimeout(resolve, 2000));

    setIsEditing(false); // Reset edit mode after successful save
  };

  const generateScheduleRows = () => {
    return schedule.map((item, dateIndex) => (
      <tr key={item.date}>
        <th scope="row">{item.date}</th>
        {item.availability.map((availability, timeIndex) => (
          <td
            key={`${item.date}-${timeIndex}`}
            className={getClassForAvailability(availability)}
            onClick={() => handleCellClick(dateIndex, timeIndex)}
          ></td>
        ))}
      </tr>
    ));
  };

  return (
    <div className='dashboard-container'>
      <Navbar />
      <div className= 'dashboard-content'>
        <SideNavbar />
        <div className="main-content">
          <h2>This week's Schedule</h2>
          <table className="table table-bordered custom-box khus">
            <thead>
              <tr>
                <th></th>
                <th>9:00 AM</th>
                <th>9:45 AM</th>
                <th>10:30 AM</th>
                <th>11:15 AM</th>
                <th>12:00 PM</th>
                <th>12:45 PM</th>
                <th>01:30 PM</th>
                <th>02:15 PM</th>
                <th>03:00 PM</th>
                <th>03:45 PM</th>
                <th>04:30 PM</th>
                <th>05:15 PM</th>
              </tr>
            </thead>
            <tbody>
              {generateScheduleRows()}
            </tbody>
          </table>

          <button type="button" className="btn btn-primary custom-button" onClick={handleEdit}>
            {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
          </button>
          {isEditing && (
            <button type="button" className="btn btn-primary custom-button" onClick={handleSubmit}>
              Submit
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
