import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Include Bootstrap CSS

const VideoCall = () => {
  const [localStream, setLocalStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const [isCalling, setIsCalling] = useState(false);
  const [callId, setCallId] = useState(null); // Track call ID for future enhancements

  // Handle user media access
  const getUserMedia = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setLocalStream(stream);
    } catch (error) {
      console.error('Error accessing user media:', error);
    }
  };

  // Initiate the call (replace with your actual calling logic)
  const initiateCall = async () => {
    setIsCalling(true);

    // Simulate generating a call ID (replace with actual API or DB call)
    const generatedCallId = 'unique-call-id';
    setCallId(generatedCallId);

    // Simulate connecting to the other participant (replace with actual signaling)
    // ... (code to connect to the other participant using callId)

    // Replace with code to receive and display the remote stream
    // setRemoteStream(remoteStreamFromOtherParticipant);
  };

  // Handle ending the call (redirect to initial page after cleanup)
  const endCall = async () => {
    setIsCalling(false);
    setRemoteStream(null);

    // Simulate disconnecting from the other participant (replace with actual signaling)
    // ... (code to disconnect from the other participant)

    // Stop the local stream
    localStream?.getTracks().forEach((track) => track.stop());

    // Redirect to initial page on call end (replace with your desired redirection logic)
    // window.location.href = '/'; // Replace with your initial page URL
  };

  // Handle component unmount to clean up resources
  useEffect(() => {
    return () => {
      if (localStream) {
        localStream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [localStream]);

  return (
    <div className="video-call-container d-flex flex-column h-100 justify-content-center align-items-center">
      {isCalling ? (
        <>
          {/* Display remote stream in a larger container */}
          <div className="remote-video-container flex-grow-1 d-flex justify-content-center align-items-center position-relative">
            {remoteStream && (
              <video
                ref={(videoEl) => (videoEl.srcObject = remoteStream)}
                autoPlay
                muted
                className="remote-video w-100 h-100 rounded-lg"
              />
            )}
            {/* Indicate no remote stream available (optional) */}
            {!remoteStream && <p className="text-white">Connecting...</p>}
          </div>

          {/* Display local stream in a smaller container */}
          <div className="local-video-container position-absolute bottom-0 right-0 m-3">
            {localStream && (
              <video ref={(videoEl) => (videoEl.srcObject = localStream)} autoPlay className="local-video rounded-lg" />
            )}
          </div>

          {/* Call controls */}
          <div className="call-controls mt-3">
            <button className="btn btn-danger" onClick={endCall}>
              End Call
            </button>
          </div>
        </>
      ) : (
        <>
          {/* Call initiation button */}
          <div className="call-initiation">
            <button className="btn btn-primary" onClick={initiateCall}>
              Start Call
            </button>
          </div>

          {/* Camera permission request (optional) */}
          {!localStream && (
            <div className="camera-permission">
              <button className="btn btn-outline-primary" onClick={getUserMedia}>
                Allow Camera Access
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default VideoCall;
