import React, { useState } from 'react';

const Documents = () => {
  const [consentModalOpen, setConsentModalOpen] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [selectedHospital, setSelectedHospital] = useState(null);

  const documents = [
    { id: 1, name: 'Patient Information.pdf', size: '2 MB' },
    { id: 2, name: 'Medical History.docx', size: '500 KB' },
    { id: 3, name: 'Latest Scans.zip', size: '10 MB' },
  ];

  const hospitals = [
    { id: 1, name: 'Hospital A' },
    { id: 2, name: 'Hospital B' },
    { id: 3, name: 'Hospital C' },
    { id: 4, name: 'Hospital D' },
  ];

  const handleDownload = (document) => {
    // Simulate download for demonstration (replace with actual download logic)
    setSelectedDocument(document);
  };

  const handleAskConsent = (document) => {
    setSelectedDocument(document);
    setConsentModalOpen(true);
  };

  const handleConsentChoice = (choice) => {
    setConsentModalOpen(false);
    setSelectedDocument(null);

    if (choice === 'otherHospitals') {
      setSelectedHospital(null);
      setConsentModalOpen(true);
    } else {
      // Simulate sending consent request based on choice (replace with actual logic)
      console.log(`Sending consent request for "${selectedDocument.name}" for ${choice}`);
    }
  };

  const handleHospitalChoice = (hospital) => {
    setSelectedHospital(hospital);
    setConsentModalOpen(false);

    // Simulate sending consent request based on hospital (replace with actual logic)
    console.log(`Sending consent request for "${selectedDocument.name}" to ${hospital.name}`);
  };

  return (
    <div className="documents">
      <h2>Documents</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Size</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {documents.map((document) => (
            <tr key={document.id}>
              <td>{document.name}</td>
              <td>{document.size}</td>
              <td>
                <button onClick={() => handleDownload(document)}>
                  Download
                </button>
                <button onClick={() => handleAskConsent(document)}>
                  Ask Consent
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {consentModalOpen && (
        <div className="consent-modal">
          <h3>Grant Access</h3>
          <p>You are requesting access to "{selectedDocument?.name}".</p>
          <button onClick={() => handleConsentChoice('self')}>For Myself</button>
          <button onClick={() => handleConsentChoice('otherHospitals')}>
            For Other Hospitals
          </button>
          <button onClick={() => setConsentModalOpen(false)}>Cancel</button>
          {selectedHospital === null && selectedDocument !== null && (
            <div>
              <h4>Select a hospital:</h4>
              <ul>
                {hospitals.map((hospital) => (
                  <li key={hospital.id} onClick={() => handleHospitalChoice(hospital)}>
                    {hospital.name}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Documents;
