import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import SideNavbar from "../../components/Doctor/sidenavbar";
import Navbar from "../../components/Doctor/Navbar";
import "../../css/Doctor/ddashboard.css";

export default function UploadSchedule() {
  // Function to generate date objects for the next 7 days including today
  const generateNext7Days = () => {
    const today = new Date();
    const next7Days = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(date.getDate() + i);
      next7Days.push(date);
    }
    return next7Days;
  };

  // Function to generate time slots from 9:00 to 15:45 with 45 minutes interval
  const generateTimeSlots = () => {
    const timeSlots = [];
    for (let hour = 9; hour <= 15; hour++) {
      for (let minute = 0; minute < 60; minute += 45) {
        timeSlots.push(`${hour}:${minute.toString().padStart(2, '0')}`);
      }
    }
    return timeSlots;
  };

  // State for schedule data
  const [schedule, setSchedule] = useState([]);

  // State for editing mode
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Initialize schedule with empty cells
    const next7Days = generateNext7Days();
    const timeSlots = generateTimeSlots();
    const initialSchedule = next7Days.map(date => ({
      date: date.toLocaleDateString(),
      availability: timeSlots.map(time => ({ time, status: 'empty' })) // Initially all cells are empty
    }));
    setSchedule(initialSchedule);
  }, []);

  // Function to toggle editing mode
  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Function to handle cell click (toggle availability)
  const handleCellClick = (dateIndex, timeIndex) => {
    if (isEditing) {
      const updatedSchedule = [...schedule];
      const currentStatus = updatedSchedule[dateIndex].availability[timeIndex].status;
      const newStatus = currentStatus === 'empty' ? 'available' : 'empty';
      updatedSchedule[dateIndex].availability[timeIndex].status = newStatus;
      setSchedule(updatedSchedule);
    }
  };

  // Function to generate table rows
  const generateScheduleRows = () => {
    return schedule.map((day, dateIndex) => (
      <tr key={day.date}>
        <th scope="row">{day.date}</th>
        {day.availability.map((slot, timeIndex) => (
          <td
            key={`${day.date}-${slot.time}`}
            className={getClassForAvailability(slot.status)}
            onClick={() => handleCellClick(dateIndex, timeIndex)}
          ></td>
        ))}
      </tr>
    ));
  };

  // Function to get CSS class for cell based on availability
  const getClassForAvailability = (status) => {
    switch (status) {
      case 'empty':
        return 'bg-secondary text-white';
      case 'available':
        return 'bg-success text-white';
      case 'booked':
        return 'bg-danger text-white';
      default:
        return '';
    }
  };

  return (
    <div className='dashboard-container'>
      <Navbar />
      <div className= 'dashboard-content'>
        <SideNavbar />
        <div className="main-content">
          <h2>This week's Schedule</h2>
          <table className="table table-bordered custom-box khus">
            <thead>
              <tr>
                <th></th>
                {generateTimeSlots().map(time => (
                  <th key={time}>{time}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {generateScheduleRows()}
            </tbody>
          </table>

          <button type="button" className="btn btn-primary custom-button" onClick={handleEdit}>
            {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
          </button>
        </div>
      </div>
    </div>
  );
}
