import React, { useState } from 'react';
import './../../css/Patient/DoctorList.css';
import SearchBar from './../../components/Patient/SearchBar';

function DoctorList() {
  // Dummy data for doctors
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDoctor, setSelectedDoctor] = useState(null); // State to track the selected doctor

  const doctors = [
    {
      id: 1,
      name: 'Dr. John Doe',
      specialty: 'Cardiologist',
      schedule: [
        { date: '2024-04-01', availability: ['busy', 'booked', 'available', 'busy', 'available', 'available', 'busy', 'booked', 'available', 'available', 'available', 'booked', 'available', 'available'] },
        // Add schedule for each day
      ]
    },
    {
      id: 2,
      name: 'Dr. Jane Smith',
      specialty: 'Dermatologist',
      schedule: [
        { date: '2024-04-01', availability: ['available', 'available', 'booked', 'available', 'busy', 'busy', 'booked', 'available', 'available', 'available', 'booked', 'busy', 'available', 'available'] },
        // Add schedule for each day
      ]
    },
    {
      id: 3,
      name: 'Dr. Michael Brown',
      specialty: 'Pediatrician',
      schedule: [
        { date: '2024-04-01', availability: ['busy', 'busy', 'booked', 'busy', 'busy', 'busy', 'booked', 'available', 'available', 'booked', 'available', 'available', 'available', 'available'] },
        // Add schedule for each day
      ]
    },
  ];

  const renderAvailability = (availability) => {
    return availability.map((slot, index) => {
      let className = '';
      switch (slot) {
        case 'available':
          className = 'available-new';
          break;
        case 'booked':
          className = 'booked-new';
          break;
        case 'busy':
          className = 'busy-new';
          break;
        default:
          className = '';
      }
      return <td key={index} className={className}></td>;
    });
  };

  const filteredDoctors = doctors.filter((doctor) =>
    doctor.specialty.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const isDoctorBusy = (doctor) => {
    return doctor.schedule.some(day => day.availability.includes('busy')); // Check if any slot is busy
  };

  const handleViewSchedule = (doctorId) => {
    setSelectedDoctor(doctorId); // Set the selected doctor ID
  };

  return (
    <div className="doctor-list-container-new">
      <SearchBar onSearch={setSearchTerm} />
      <h2 style={{textAlign: 'center'}}>Available Doctors</h2>
      <ul>
        {filteredDoctors.map((doctor, index) => (
          <li key={index}>
            <h3>{doctor.name}</h3>
            <p>{doctor.specialty}</p>
            {selectedDoctor === doctor.id && (
              <table className="availability-table-new">
                <thead>
                  <tr>
                    <th>Date</th>
                    {Array.from({ length: 14 }, (_, i) => i + 8).map((hour) => (
                      <th key={hour}>{hour}:00 - {hour + 1}:00</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {doctor.schedule.map((day, idx) => (
                    <tr key={idx}>
                      <td>{day.date}</td>
                      {renderAvailability(day.availability)}
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
            {selectedDoctor !== doctor.id && (
              <button className={isDoctorBusy(doctor) ? 'doc-list-wala-button-new busy-new' : 'doc-list-wala-button-new'} onClick={() => handleViewSchedule(doctor.id)}>
                View Schedule
              </button>
            )}
            <br></br>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DoctorList;
