import React, { useState } from 'react';
import './../../css/Patient/MyDocuments.css';

const MyDocuments = () => {
  const [documents, setDocuments] = useState(["Document1.pdf", "Document2.docx", "Document3.jpg"]);
  const [showConsentModal, setShowConsentModal] = useState(false);

  const handleDownload = (fileName) => {
    console.log('Downloading file:', fileName);
    // Here you can implement the download logic
  };

  const handleAskForConsent = () => {
    setShowConsentModal(true);
  };

  const handleAskForSelfConsent = () => {
    console.log('Ask for self consent clicked');
    // Here you can implement the logic for asking for self consent
    setShowConsentModal(false);
  };

  const handleAskForOtherHospitalsConsent = () => {
    console.log('Ask for other hospitals consent clicked');
    // Here you can implement the logic for asking for other hospitals consent
    setShowConsentModal(false);
  };

  return (
    <div className="patient-my-documents-container">
      <h2>My Documents</h2>
      <div className="patient-previous-documents">
        <h3>Previous Documents</h3>
        <table className="patient-documents-table">
          <thead>
            <tr>
              <th>Document Name</th>
              <th>Download</th>
              <th>Ask for Consent</th>
            </tr>
          </thead>
          <tbody>
            {documents.map((fileName, index) => (
              <tr key={index}>
                <td>{fileName}</td>
                <td><button onClick={() => handleDownload(fileName)}>Download</button></td>
                <td><button onClick={handleAskForConsent}>Ask for Consent</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Consent Modal */}
      {showConsentModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowConsentModal(false)}>&times;</span>
            <h2>Ask for Consent</h2>
            <button onClick={handleAskForSelfConsent}>Ask for Self</button>
            <button onClick={handleAskForOtherHospitalsConsent}>Ask for Other Hospitals</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyDocuments;
