import React, { useState } from 'react';
import './../../css/Patient/DoctorList.css';

function DoctorList() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedHospital, setSelectedHospital] = useState(null);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [isBookingMode, setIsBookingMode] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [lockedSlots, setLockedSlots] = useState([]);
  const [isScheduleCollapsed, setIsScheduleCollapsed] = useState(false);

  const hospitals = [
    {
      id: 1,
      name: 'Hospital 1',
      doctors: [
        {
          id: 1,
          name: 'Dr. John Doe',
          specialty: 'Cardiologist',
          schedule: {
            slots: [
              [2024, 4, 1, 11, 15],
              [2024, 4, 3, 15, 45],
              [2024, 4, 4, 15, 0],
              [2024, 4, 2, 15, 45],
              [2024, 4, 5, 15, 0]
            ]
          },
          
        },
        {
          id: 2,
          name: 'Dr. Jane Smith',
          specialty: 'Dermatologist',
          schedule: {
            slots: [
              [2024, 4, 1, 11, 15],
              [2024, 4, 3, 15, 45],
              [2024, 4, 4, 15, 0],
              [2024, 4, 2, 15, 45],
              [2024, 4, 5, 15, 0]
            ]
          },
          
        }
      ]
    }
  ];

  const filteredHospitals = hospitals.filter((hospital) =>
    hospital.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleViewDoctors = (hospitalId) => {
    setSelectedHospital(hospitalId);
    setSelectedDoctor(null);
    setIsScheduleCollapsed(false);
  };

  const handleViewSchedule = (doctorId) => {
    setSelectedDoctor(doctorId);
    setIsBookingMode(false);
  };

  const handleCellClick = (date, index) => {
    if (isBookingMode) {
      const selectedHospitalData = hospitals.find(hospital => hospital.id === selectedHospital);
      if (selectedHospitalData) {
        const selectedDoctorData = selectedHospitalData.doctors.find(doctor => doctor.id === selectedDoctor);
        if (selectedDoctorData) {
          const isGreen = selectedDoctorData.schedule.slots.some(slot => {
            return slot[0] === 2024 && slot[1] === 4 && slot[2] === date && slot[3] === index;
          });
          if (isGreen && !lockedSlots.includes(`${date}_${index}`)) {
            setSelectedSlot({ date, index });
          }
        }
      }
    }
  };

  const handleLockSlot = () => {
    if (selectedSlot) {
      const slotKey = `${selectedSlot.date}_${selectedSlot.index}`;
      setLockedSlots([...lockedSlots, slotKey]);
      setSelectedSlot(null);
    }
  };

  const handleToggleBookingMode = () => {
    setIsBookingMode(!isBookingMode);
    setSelectedSlot(null);
  };

  const renderAvailability = (date) => {
    const selectedDoctorData = hospitals[selectedHospital - 1]?.doctors.find(doctor => doctor.id === selectedDoctor);
    if (selectedDoctorData) {
      const doctorSlots = selectedDoctorData.schedule.slots.filter(slot => slot[2] === date);
      return Array.from({ length: 13 }, (_, i) => i).map((index) => {
        const isGreen = doctorSlots.some(slot => slot[3] === index);
        const isLocked = lockedSlots.includes(`${date}_${index}`);
        return (
          <td
            key={index}
            className={isBookingMode && isGreen && !isLocked ? 'available-new clickable' : 'busy-new'}
            onClick={() => handleCellClick(date, index)}
          >
            {isBookingMode && isGreen && !isLocked && selectedSlot && selectedSlot.date === date && selectedSlot.index === index && 'Selected'}
            {isLocked && 'Locked'}
          </td>
        );
      });
    }
    return null;
  };

  return (
    <div className="doctor-list-container-new">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by Hospital..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <h2 style={{textAlign: 'center'}}>Available Hospitals</h2>
      <ul>
        {filteredHospitals.map((hospital, index) => (
          <li key={index}>
            <h3>{hospital.name}</h3>
            {selectedHospital === hospital.id && (
              <>
                <h4>Doctors:</h4>
                <ul>
                  {hospital.doctors.map((doctor, idx) => (
                    <li key={idx}>
                      <h5>{doctor.name}</h5>
                      <p>{doctor.specialty}</p>
                      {selectedDoctor === doctor.id && (
                        <>
                          <table className="availability-table-new">
                            <thead>
                              <tr>
                                <th>Date</th> 
                                <th>09:00 - 09:45</th>
                                <th>09:45 - 10:30</th>
                                <th>10:30 - 11:15</th>
                                <th>11:15 - 12:00</th>
                                <th>12:00 - 12:45</th>
                                <th>12:45 - 13:30</th>
                                <th>13:30 - 14:15</th>
                                <th>14:15 - 15:00</th>
                                <th>15:00 - 15:45</th>
                                <th>15:45 - 16:30</th>
                                <th>16:30 - 17:15</th>
                                <th>17:15 - 18:00</th>
                              </tr>
                            </thead>
                            <tbody>
                              {doctor.schedule.slots.map((slot, idx) => (
                                <tr key={idx}>
                                  <td>{slot[2]}/04/2024</td>
                                  {renderAvailability(slot[2])}
                                </tr>
                              ))}
                            </tbody>
                          </table>
                          <button className="doc-list-wala-button-new" onClick={handleToggleBookingMode}>
                            {isBookingMode ? 'Cancel Booking' : 'Book Appointment'}
                          </button>
                          {selectedSlot && (
                            <button className="doc-list-wala-button-new" onClick={handleLockSlot}>
                              Lock Slot
                            </button>
                          )}
                        </>
                      )}
                      {selectedDoctor !== doctor.id && (
                        <button className='doc-list-wala-button-new busy-new' onClick={() => handleViewSchedule(doctor.id)}>
                          View Schedule
                        </button>
                      )}
                      <br />
                    </li>
                  ))}
                </ul>
              </>
            )}
            {selectedHospital !== hospital.id && (
              <button className="doc-list-wala-button-new" onClick={() => handleViewDoctors(hospital.id)}>
                View Doctors
              </button>
            )}
            <br />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DoctorList;
