import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import SideNavbar from "../../components/Doctor/sidenavbar";
import Navbar from "../../components/Doctor/Navbar";
import "../../css/Doctor/ddashboard.css";

export default function UploadSchedule() {
  // Define dummy schedule data
  const dummySchedule = {
    "slots": [
      [2024, 4, 5, 11, 15],
      [2024, 4, 6, 15, 45],
      [2024, 4, 7, 15, 0],
      [2024, 4, 6, 15, 45],
      [2024, 4, 5, 15, 0]
    ],
    "appointments": [
      { id:1, date: [2024,4,5], time: '09:00', patientName: 'John Doe' },
      { id:2, date: [2024,4,6], time: '10:30', patientName: 'somebody someone' },
      { id:3, date: [2024,4,7], time: '11:15', patientName: 'Alice Smith' },
      { id:4, date: [2024,4,8], time: '12:00', patientName: 'Bob Johnson' },
      { id:5, date: [2024,4,6], time: '09:00', patientName: 'John Doe' },
    ]
  };

  // State for schedule data
  const [schedule, setSchedule] = useState(dummySchedule);

  // State for editing mode
  const [isEditing, setIsEditing] = useState(false);

  // Function to toggle editing mode
  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Function to handle cell click (toggle availability)
  const handleCellClick = (date, time) => {
    // Handle cell click logic here if needed
  };

  const generateScheduleRows = () => {
    if (!schedule || !schedule.slots) return null;

    return schedule.slots.map((slot, index) => (
      <tr key={index}>
        <th scope="row">{`${slot[0]}-${slot[1]}-${slot[2]}`}</th>
        {[...Array(10)].map((_, timeIndex) => {
          const timeSlot = `${9 + Math.floor(timeIndex / 4)}:${(timeIndex % 4) * 45 === 0 ? '00' : (timeIndex % 4) * 45}`;
          const isBooked = schedule.appointments.some(appointment =>
            appointment.date[0] === slot[0] &&
            appointment.date[1] === slot[1] &&
            appointment.date[2] === slot[2] &&
            appointment.time === timeSlot
          );
          return (
            <td
              key={`${index}-${timeIndex}`}
              className={isBooked ? 'table-danger bg-danger text-dark' : 'table-success bg-success text-white'}
              onClick={() => handleCellClick(slot, timeSlot)}
            ></td>
          );
        })}
      </tr>
    ));
  };

  return (
    <div className='dashboard-container'>
      <Navbar />
      <div className= 'dashboard-content'>
        <SideNavbar />
        <div className="main-content">
          <h2>This week's Schedule</h2>
          <table className="table table-bordered custom-box khus">
            <thead>
              <tr>
                <th></th>
                {[...Array(10)].map((_, index) => (
                  <th key={index}>{`${9 + Math.floor(index / 4)}:${(index % 4) * 15 === 0 ? '00' : (index % 4) * 15}`}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {generateScheduleRows()}
            </tbody>
          </table>

          <button type="button" className="btn btn-primary custom-button" onClick={handleEdit}>
            {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
          </button>
          {isEditing && (
            <button type="button" className="btn btn-primary custom-button" onClick={handleSubmit}>
              Submit
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
