import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import SideNavbar from "../../components/Doctor/sidenavbar";
import Navbar from "../../components/Doctor/Navbar";
import "../../css/Doctor/ddashboard.css";

export default function UploadSchedule() {
  const getNextDates = () => {
    const today = new Date();
    const dates = [];

    for (let i = 0; i < 7; i++) {
      const nextDate = new Date(today);
      nextDate.setDate(today.getDate() + i);
      dates.push(nextDate.toISOString().split('T')[0]);
    }

    return dates;
  };
  // Define dummy schedule data
  const scheduleData = {
    "slots": [
      [2024, 4, 5, 11, 15],
      [2024, 4, 6, 15, 45],
      [2024, 4, 7, 15, 0],
      [2024, 4, 2, 15, 45],
      [2024, 4, 5, 15, 0]
    ],
    "appointments": [
      { id: 1, date: getNextDates()[0], time: '09:00', patientName: 'John Doe' },
      { id: 2, date: getNextDates()[4], time: '10:30', patientName: 'somebody someone' },
      { id: 3, date: getNextDates()[1], time: '11:15', patientName: 'Alice Smith' },
      { id: 4, date: getNextDates()[3], time: '12:00', patientName: 'Bob Johnson' },
      { id: 5, date: getNextDates()[5], time: '09:00', patientName: 'John Doe' },
    ]
  };

  // State for schedule data
  const [schedule, setSchedule] = useState([]);

  // State for editing mode
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Initialize schedule with dummy data
    setSchedule(scheduleData);
  }, []);

  // Function to toggle editing mode
  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Function to handle cell click (toggle availability)
  const handleCellClick = (dateIndex, timeIndex) => {
    // Do nothing if not in editing mode
    if (!isEditing) return;

    const updatedSchedule = { ...schedule };

    // Toggle availability only if not booked
    if (updatedSchedule.availability[dateIndex][timeIndex] !== 'booked') {
      updatedSchedule.availability[dateIndex][timeIndex] =
        updatedSchedule.availability[dateIndex][timeIndex] === 'available' ? 'busy' : 'available';

      setSchedule(updatedSchedule);
    }
  };

  const generateScheduleRows = () => {
    return schedule.slots.map((slot, dateIndex) => (
      <tr key={dateIndex}>
        <th scope="row">{slot[0]}/{slot[1]}/{slot[2]}</th>
        {slot.slice(3).map((time, timeIndex) => {
          const availability = getAvailability(dateIndex, timeIndex);
          return (
            <td
              key={`${dateIndex}-${timeIndex}`}
              className={getClassForAvailability(availability)}
              onClick={() => handleCellClick(dateIndex, timeIndex)}
            ></td>
          );
        })}
      </tr>
    ));
  };

  // Function to get availability for a given slot
  const getAvailability = (dateIndex, timeIndex) => {
    const currentTime = schedule.slots[dateIndex][timeIndex + 3];
    for (const appointment of schedule.appointments) {
      if (appointment.date === `${schedule.slots[dateIndex][0]}-${schedule.slots[dateIndex][1]}-${schedule.slots[dateIndex][2]}` && appointment.time === formatTime(currentTime)) {
        return 'booked';
      }
    }
    return 'available';
  };

  // Function to format time
  const formatTime = (time) => {
    const hours = Math.floor(time / 60);
    const minutes = time % 60;
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
  };

  // Function to get CSS class for cell based on availability
  const getClassForAvailability = (availability) => {
    switch (availability) {
      case 'available':
        return 'table-success bg-success text-white';
      case 'booked':
        return 'table-danger bg-danger text-dark';
      case 'busy':
        return 'table-secondary bg-secondary text-white';
      default:
        return '';
    }
  };

  // Function to simulate saving the schedule (replace with actual logic)
  const handleSubmit = async () => {
    alert('Saving schedule...');

    // Replace with your actual saving logic (e.g., API call)
    // This example simulates a successful save after 2 seconds
    await new Promise((resolve) => setTimeout(resolve, 2000));

    setIsEditing(false); // Reset edit mode after successful save
  };



  return (
    <div className='dashboard-container'>
      <Navbar />
      <div className='dashboard-content'>
        <SideNavbar />
        <div className="main-content">
          <h2>This week's Schedule</h2>
          <table className="table table-bordered custom-box khus">
            <thead>
              <tr>
                <th></th>
                <th>9:00 AM</th>
                <th>9:45 AM</th>
                <th>10:30 AM</th>
                <th>11:15 AM</th>
                <th>12:00 PM</th>
                <th>12:45 PM</th>
                <th>01:30 PM</th>
                <th>02:15 PM</th>
                <th>03:00 PM</th>
                <th>03:45 PM</th>
              </tr>
            </thead>
            <tbody>
              {generateScheduleRows()}
            </tbody>
          </table>

          <button type="button" className="btn btn-primary custom-button" onClick={handleEdit}>
            {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
          </button>
          {isEditing && (
            <button type="button" className="btn btn-primary custom-button" onClick={handleSubmit}>
              Submit
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
