import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import SideNavbar from "../../components/Doctor/sidenavbar";
import Navbar from "../../components/Doctor/Navbar";
import "../../css/Doctor/ddashboard.css";

export default function UploadSchedule() {
  const dummySchedule = {
    "slots": [
      [2024, 4, 5, 11, 15],
      [2024, 4, 6, 15, 45],
      [2024, 4, 7, 15, 0],
      [2024, 4, 6, 15, 45],
      [2024, 4, 5, 15, 0]
    ]
  };

  const generateScheduleFromDummy = (dummySchedule) => {
    const schedule = {};
    dummySchedule.slots.forEach(slot => {
      const [year, month, day, hour, minute] = slot;
      const date = new Date(year, month - 1, day);
      const dateString = date.toDateString();
      const timeString = `${hour}:${minute === 0 ? '00' : minute} ${hour >= 12 ? 'PM' : 'AM'}`;
      if (!schedule[dateString]) {
        schedule[dateString] = [];
      }
      schedule[dateString].push(timeString);
    });
    return schedule;
  };

  const initialSchedule = generateScheduleFromDummy(dummySchedule);

  const [schedule, setSchedule] = useState(initialSchedule);
  const [isEditing, setIsEditing] = useState(false);

  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  const handleCellClick = () => {
    // Handle cell click logic here
  };

  const handleUpload = () => {
    // Handle upload logic here
  };

  const handleSubmit = async () => {
    // Handle submit logic here
  };

  useEffect(() => {
    // Additional logic if needed on component mount
  }, []);

  return (
    <div className='dashboard-container'>
      <Navbar />
      <div className='dashboard-content'>
        <SideNavbar />
        <div className="main-content">
          <h2>This Week's Schedule</h2>
          <table className="table table-bordered custom-box">
            <thead>
              <tr>
                <th>Date</th>
                {[...Array(10)].map((_, index) => (
                  <th key={index}>{index === 0 ? '9:00 AM' : `${index + 9}:00 AM`}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {Object.entries(schedule).map(([date, slots]) => (
                <tr key={date}>
                  <th scope="row">{date}</th>
                  {[...Array(10)].map((_, index) => (
                    <td
                      key={`${date}-${index}`}
                      className={slots.includes(`${index + 9}:00 AM`) ? 'table-success bg-success text-white' : 'table-secondary'}
                      onClick={() => isEditing && handleCellClick()}
                    >
                      {isEditing ? '' : slots.includes(`${index + 9}:00 AM`) ? `${index + 9}:00 AM` : ''}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
          <button type="button" className="btn btn-primary custom-button" onClick={handleEdit}>
            {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
          </button>
          {isEditing ? (
            <button type="button" className="btn btn-primary custom-button" onClick={handleSubmit}>
              Submit
            </button>
          ) : (
            <button type="button" className="btn btn-success custom-button2" onClick={handleUpload}>
              Upload New Schedule
            </button>)}
        </div>
      </div>
    </div>
  );
}
