import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import SideNavbar from "../../components/Doctor/sidenavbar";
import Navbar from "../../components/Doctor/Navbar";
import "../../css/Doctor/ddashboard.css";

export default function UploadSchedule() {
  // Define dummy schedule data
  const dummySchedule = {
    "slots": [
      [2024, 4, 5, 11, 15],
      [2024, 4, 6, 15, 45],
      [2024, 4, 7, 15, 0],
      [2024, 4, 6, 15, 45],
      [2024, 4, 5, 15, 0]
    ]
  };

  // State for schedule data
  const [schedule, setSchedule] = useState([]);

  // State for editing mode
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Initialize schedule with empty data structure
    const currentDate = new Date();
    const next7Days = [...Array(7)].map((_, index) => {
      const date = new Date();
      date.setDate(currentDate.getDate() + index);
      return date;
    });
    const initialSchedule = next7Days.map(date => ({
      date: date.toISOString().split('T')[0],
      slots: Array.from({ length: 10 }, (_, index) => {
        const time = new Date(date);
        time.setHours(9 + Math.floor(index / 2), index % 2 === 0 ? 0 : 45);
        return time.toISOString().split('T')[1].slice(0, 5);
      }),
      appointments: []
    }));
    setSchedule(initialSchedule);
  }, []);

  useEffect(() => {
    // Map dummy schedule data to the schedule
    const updatedSchedule = [...schedule];
    dummySchedule.slots.forEach(slot => {
      const [year, month, day, hour, minute] = slot;
      const slotDate = new Date(year, month - 1, day, hour, minute);
      const scheduleDate = new Date(slotDate.toDateString());
      const dateIndex = schedule.findIndex(item => item.date === scheduleDate.toISOString().split('T')[0]);
      if (dateIndex !== -1) {
        const timeIndex = Math.floor((slotDate.getHours() - 9) * 2 + slotDate.getMinutes() / 45);
        if (timeIndex >= 0 && timeIndex < updatedSchedule[dateIndex].slots.length) {
          updatedSchedule[dateIndex].appointments.push(timeIndex);
        }
      }
    });
    setSchedule(updatedSchedule);
  }, [dummySchedule]);

  // Function to toggle editing mode
  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  const generateScheduleRows = () => {
    return schedule.map((item, dateIndex) => (
      <tr key={item.date}>
        <th scope="row">{item.date}</th>
        {item.slots.map((timeSlot, timeIndex) => (
          <td
            key={`${item.date}-${timeIndex}`}
            className={getClassForSlot(timeIndex, item.appointments)}
            onClick={() => handleCellClick(dateIndex, timeIndex)}
          ></td>
        ))}
      </tr>
    ));
  };

  // Function to get CSS class for cell based on availability
  const getClassForSlot = (timeIndex, appointments) => {
    if (appointments.includes(timeIndex)) {
      return 'table-danger bg-danger text-dark';
    }
    return 'table-success bg-success text-white';
  };

  // Function to handle cell click (toggle availability)
  const handleCellClick = (dateIndex, timeIndex) => {
    if (isEditing) {
      const updatedSchedule = [...schedule];
      const appointments = [...updatedSchedule[dateIndex].appointments];
      const appointmentIndex = appointments.indexOf(timeIndex);
      if (appointmentIndex === -1) {
        appointments.push(timeIndex);
      } else {
        appointments.splice(appointmentIndex, 1);
      }
      updatedSchedule[dateIndex].appointments = appointments;
      setSchedule(updatedSchedule);
    }
  };

  // Function to simulate saving the schedule (replace with actual logic)
  const handleSubmit = async () => {
    alert('Saving schedule...');

    // Replace with your actual saving logic (e.g., API call)
    // This example simulates a successful save after 2 seconds
    await new Promise((resolve) => setTimeout(resolve, 2000));

    setIsEditing(false); // Reset edit mode after successful save
  };

  return (
    <div className='dashboard-container'>
      <Navbar />
      <div className= 'dashboard-content'>
        <SideNavbar />
        <div className="main-content">
          <h2>This week's Schedule</h2>
          <table className="table table-bordered custom-box khus">
            <thead>
              <tr>
                <th></th>
                <th>9:00 AM</th>
                <th>9:45 AM</th>
                <th>10:30 AM</th>
                <th>11:15 AM</th>
                <th>12:00 PM</th>
                <th>12:45 PM</th>
                <th>01:30 PM</th>
                <th>02:15 PM</th>
                <th>03:00 PM</th>
                <th>03:45 PM</th>
              </tr>
            </thead>
            <tbody>
              {generateScheduleRows()}
            </tbody>
          </table>

          <button type="button" className="btn btn-primary custom-button" onClick={handleEdit}>
            {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
          </button>
          {isEditing && (
            <button type="button" className="btn btn-primary custom-button" onClick={handleSubmit}>
              Submit
            </button>
          )}
        </div>
      </div>
    </div>
  );
}
