// SideNavbar.js
import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

export default function SideNavbar() {
  const location = useLocation();

  // Check if the current route matches "/ddashboard/anyname"
  const isDashboardPage = location.pathname.startsWith('/ddashboard');

  // Define dynamic styles for the sidebar
  const sidebarStyles = {
    position: 'fixed',
    top: 0,
    left: isDashboardPage ? 0 : '-250px', // Hide the sidebar when not on "/ddashboard/anyname"
    height: '100%',
    width: '250px',
    transition: 'left 0.3s ease', // Add transition for smooth animation
    backgroundColor: 'dark',
    color: 'white',
  };

  return isDashboardPage ? (
    <div style={sidebarStyles}>
      <ul className="nav flex-column">
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard">
            Home
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard/upload">
            Upload Schedule
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/join-meetings">
            Join Meetings
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/chats">
            Chats
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard/appointment">
            View Previous Appointments
          </NavLink>
        </li>
      </ul>
    </div>
  ) : null;
}
