import React, { useState } from 'react';

const dummyData = [
  { senderName: 'Alice', message: 'Hi everyone!' },
  { senderName: 'Bob', message: 'Hey Alice, how are you?' },
  { senderName: 'Charlie', message: 'Welcome to the chat!' },
];

function Chats() {
  const [messages, setMessages] = useState(dummyData); // Use dummy data initially

  const handleSendMessage = (event) => {
    event.preventDefault();
    const message = event.target.elements.message.value;
    const senderName = event.target.elements.senderName.value;
    setMessages([...messages, { message, senderName }]); // Add new message to state
    event.target.elements.message.value = ''; // Clear message input
  };

  return (
    <div className="chat-app">
      <header className="chat-header">
        <h1>Chat Room</h1>
      </header>
      <main className="chat-main">
        <ul className="chat-history">
          {messages.map((message, index) => (
            <li key={index}>
              <span className="sender-name">{message.senderName}: </span>
              <span className="message-content">{message.message}</span>
            </li>
          ))}
        </ul>
      </main>
      <footer className="chat-footer">
        <form className="message-form" onSubmit={handleSendMessage}>
          <input type="text" name="senderName" placeholder="Your Name" required />
          <input type="text" name="message" placeholder="Type your message..." required />
          <button type="submit">Send</button>
        </form>
      </footer>
    </div>
  );
}

export default Chats;
