import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

export default function SideNavbar() {
  const location = useLocation();

  // Check if the current path starts with "/ddashboard/"
  const isDashboardPage = location.pathname.startsWith('/ddashboard');

  // Apply conditional styling for visibility
  const sidebarStyle = {
    height: '100%',
    width: isDashboardPage ? '250px' : '0',
    overflowX: 'hidden',
    transition: 'width 0.5s',
  };

  return (
    <div className="position-fixed bg-dark text-light" style={sidebarStyle}>
      <ul className="nav flex-column">
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard">
            Home
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard/upload">
            Upload Schedule
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard/join-meetings">
            Join Meetings
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard/chats">
            Chats
          </NavLink>
        </li>
        <li className="nav-item">
          <NavLink className="nav-link text-light" to="/ddashboard/appointment">
            View Previous Appointments
          </NavLink>
        </li>
      </ul>
    </div>
  );
}
