import React, { useState } from 'react';
import './../css/PatientLogin.css';
import logo from './../images/patient-login.png';
import { useNavigate } from 'react-router-dom';
import videobg from './../Animation/bg.mp4';

const PatientLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();

  const handleGetOTP = () => {
    fetch('/api/v1/auth/generate/patient/otp', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username: username }),
    })
    .then(response => response.json())
    .then(data => {
      alert("OTP has been sent to your email."); // Assuming the OTP is sent to the user's email
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const handleLogin = () => {
    fetch('/api/v1/auth/patient/authenticate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username: username,
        otp: otp,
        password: password,
      }),
    })
    .then(response => response.json())
    .then(data => {
      localStorage.setItem('token', data.token);
      alert('Login successful!');
      navigate('/patientdashboard');
    })
    .catch(error => {
      console.error('Error:', error);
      alert('Invalid username, password, or OTP');
    });
  };

  const handleSignUp = () => {
    // Redirect to SignUp page
    navigate('/signup');
  };

  return (
    <div className="container">
      <video autoPlay muted loop className="video-bg">
        <source src={videobg} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="login-container">
        <img src={logo} alt="Patient Logo" className="patient-logo" />
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
        <input type="text" placeholder="Enter OTP" value={otp} onChange={(e) => setOtp(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <button onClick={handleGetOTP}>Get OTP</button>
        <button onClick={handleLogin}>Login</button>
        <button className="signup-btn" onClick={handleSignUp}>Don't have an account? Register.</button>
      </div>
    </div>
  );
};

export default PatientLogin;
