import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

export default function UploadSchedule() {
  const initialSchedule = {
    monday: {
      '9:00 AM': true,
      '10:00 AM': false,
      // Add more timeslots as needed
    },
    tuesday: {
      // ...
    },
    // ...
  };

  const [schedule, setSchedule] = useState(initialSchedule);
  const [isEditing, setIsEditing] = useState(false);

  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  const handleCellClick = (day, time) => {
    setSchedule((prevSchedule) => ({
      ...prevSchedule,
      [day]: {
        ...prevSchedule[day],
        [time]: !prevSchedule[day][time],
      },
    }));
  };

  const generateEmptySchedule = () => {
    const days = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday'];
    const timeslots = Array.from({ length: 14 }, (_, index) => {
      const hour = Math.floor(index / 2) + 9; // Hours from 9 AM to 10 PM
      const minute = index % 2 === 0 ? '00' : '30';
      return `${hour}:${minute}`;
    });

    return days.reduce((acc, day) => {
      acc[day] = timeslots.reduce((acc2, time) => {
        acc2[time] = false; // All cells grey (unavailable) in upload mode
        return acc2;
      }, {});
      return acc;
    }, {});
  };

  const handleUpload = () => {
    setSchedule(generateEmptySchedule());
    setIsEditing(true);
  };

  const handleSubmit = async () => {
    alert('Saving schedule...');
    await new Promise((resolve) => setTimeout(resolve, 2000));
    setIsEditing(false);
  };

  return (
    <div className="container mt-4" style={{ marginLeft: '250px', marginTop: '56px' }}>
      <h2>Upload Schedule</h2>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th></th>
            {Array.from({ length: 14 }, (_, index) => {
              const hour = Math.floor(index / 2) + 9; // Hours from 9 AM to 10 PM
              const suffix = index % 2 === 0 ? 'AM' : 'PM';
              return <th key={index}>{`${hour}:00 ${suffix}`}</th>;
            })}
          </tr>
        </thead>
        <tbody>
          {Object.entries(schedule).map(([day, timeslots]) => (
            <tr key={day}>
              <th scope="row">{day}</th>
              {Object.entries(timeslots).map(([time, available]) => (
                <td
                  key={`${day}-${time}`}
                  className={
                    available ? 'table-success bg-success text-white' : 'table-secondary'
                  }
                  onClick={() => (isEditing ? handleCellClick(day, time) : null)}
                >
                  {isEditing ? '' : time}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <button type="button" className="btn btn-primary me-2" onClick={handleEdit}>
        {isEditing ? 'Cancel Edit' : 'Edit Schedule'}
      </button>
      <button type="button" className="btn btn-success" onClick={handleUpload}>
        Upload New Schedule
      </button>
      {isEditing && (
        <button type="button" className="btn btn-primary" onClick={handleSubmit}>
          Submit
        </button>
      )}
    </div>
  );
}
