import React, { useState } from 'react';

export default function UploadSchedule() {
  // Dummy schedule data (replace with your data source)
  const initialSchedule = {
    title: '',
    description: '',
    // Add schedule details like days, timings, etc.
  };

  // State for schedule data
  const [schedule, setSchedule] = useState(initialSchedule);

  // State for editing mode
  const [isEditing, setIsEditing] = useState(false);

  // Function to handle schedule changes
  const handleChange = (event) => {
    setSchedule({ ...schedule, [event.target.name]: event.target.value });
  };

  // Function to toggle editing mode
  const handleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Function to simulate uploading the schedule (replace with actual logic)
  const handleUpload = () => {
    alert('Uploading schedule...');
    // Implement logic to upload schedule data (e.g., API call)
  };

  return (
    <div className="container mt-4" style={{ marginLeft: '250px', marginTop: '56px' }}>
      <h2>Upload Schedule</h2>

      {isEditing ? (
        <form>
          <div className="mb-3">
            <label htmlFor="title" className="form-label">Schedule Title</label>
            <input
              type="text"
              className="form-control"
              id="title"
              name="title"
              value={schedule.title}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="description" className="form-label">Description</label>
            <textarea
              className="form-control"
              id="description"
              name="description"
              value={schedule.description}
              onChange={handleChange}
              rows="3"
              required
            />
          </div>
          {/* Add form fields for other schedule details */}
          <button type="button" className="btn btn-primary me-2" onClick={handleUpload}>
            Upload Schedule
          </button>
          <button type="button" className="btn btn-secondary" onClick={handleEdit}>
            Cancel Edit
          </button>
        </form>
      ) : (
        <div>
          {/* Display your schedule data here (e.g., using a table or list) */}
          <p>Schedule details will be displayed here.</p>
          <button type="button" className="btn btn-primary me-2" onClick={handleEdit}>
            Edit Schedule
          </button>
          <button type="button" className="btn btn-success" onClick={handleUpload}>
            Upload New Schedule
          </button>
        </div>
      )}
    </div>
  );
}
