import React, { useState, useEffect } from 'react';

const dummyData = [
  { message: 'Hello there!', senderName: 'John Doe' },
  { message: 'Hi! How can I help you today?', senderName: 'Support Agent' },
];

function Chats() {
  const [messages, setMessages] = useState(dummyData);
  const [newMessage, setNewMessage] = useState('');
  const [newSenderName, setNewSenderName] = useState('');

  useEffect(() => {
    // Simulate loading messages from an API (replace with actual API calls)
    setTimeout(() => {
      // setMessages([...messages, ...dummyData]); // uncomment if you want to append dummy data
    }, 1000);
  }, []);

  const handleSendMessage = (event) => {
    event.preventDefault();
    if (newMessage.trim() === '') {
      alert('Please enter a message.');
      return;
    }

    setMessages([...messages, { message: newMessage, senderName: newSenderName }]);
    setNewMessage(''); // Clear message input
  };

  return (
    <div className="chats-container"  style={{ marginLeft: '250px', marginTop: '56px' }}>
      <h1>Chats</h1>
      <ul className="messages-list">
        {messages.map((message, index) => (
          <li key={index}>
            <strong>{message.senderName}: </strong> {message.message}
          </li>
        ))}
      </ul>
      <form className="message-form" onSubmit={handleSendMessage}>
        <input
          type="text"
          name="senderName"
          placeholder="Your Name"
          value={newSenderName}
          onChange={(e) => setNewSenderName(e.target.value)}
          required
        />
        <input
          type="text"
          name="message"
          placeholder="Type your message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          required
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default Chats;
